import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

export default function EventForm({ token, edit }) {
  const { id } = useParams();
  const [form, setForm] = useState({ title: '', description: '', date: '' });
  const navigate = useNavigate();

  useEffect(() => {
    if (edit && id) {
      axios.get(`http://localhost:5000/api/events/${id}`)
        .then(res => setForm({
          title: res.data.title,
          description: res.data.description,
          date: res.data.date?.slice(0, 16)
        }));
    }
  }, [edit, id]);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    const config = { headers: { Authorization: `Bearer ${token}` } };
    if (edit) {
      await axios.put(`http://localhost:5000/api/events/${id}`, form, config);
    } else {
      await axios.post('http://localhost:5000/api/events', form, config);
    }
    navigate('/');
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{edit ? 'Edit' : 'Create'} Event</h2>
      <input name="title" placeholder="Title" value={form.title} onChange={handleChange} required />
      <textarea name="description" placeholder="Description" value={form.description} onChange={handleChange} required />
      <input name="date" type="datetime-local" value={form.date} onChange={handleChange} required />
      <button type="submit">{edit ? 'Update' : 'Create'}</button>
    </form>
  );
}
