import React from 'react';
import { BrowserRouter, Routes, Route, Navigate, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import EventList from './pages/EventList';
import EventDetails from './pages/EventDetails';
import EventForm from './pages/EventForm';

function App() {
  const [token, setToken] = React.useState(localStorage.getItem('token'));

  const handleLogin = (token) => {
    localStorage.setItem('token', token);
    setToken(token);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
  };

  return (
    <BrowserRouter>
      <nav className="fixed top-0 left-0 w-full z-50 backdrop-blur-md bg-white/10 border-b border-white/20 shadow-lg py-3 px-0">
        <div className="max-w-7xl mx-auto flex justify-between items-center px-6">
          <Link to="/" className="text-xl font-extrabold text-white tracking-tight drop-shadow-lg hover:text-[#7f5af0] transition">Event Management</Link>
          <div className="space-x-6 text-lg font-semibold">
            <Link to="/" className="text-white hover:text-[#7f5af0] transition">Events</Link>
            {token ? (
              <button onClick={handleLogout} className="text-white hover:text-red-400 transition">Logout</button>
            ) : (
              <>
                <Link to="/login" className="text-white hover:text-[#7f5af0] transition">Login</Link>
                <Link to="/register" className="text-white hover:text-[#7f5af0] transition">Register</Link>
              </>
            )}
          </div>
        </div>
      </nav>
      <div className="pt-20">
        <Routes>
          <Route path="/" element={<EventList token={token} />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register onLogin={handleLogin} />} />
          <Route path="/events/:id" element={<EventDetails token={token} />} />
          <Route path="/create" element={token ? <EventForm token={token} /> : <Navigate to="/login" />} />
          <Route path="/edit/:id" element={token ? <EventForm token={token} edit /> : <Navigate to="/login" />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
