import React from 'react';
import { BrowserRouter, Routes, Route, Navigate, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import EventList from './pages/EventList';
import EventDetails from './pages/EventDetails';
import EventForm from './pages/EventForm';

function App() {
  const [token, setToken] = React.useState(localStorage.getItem('token'));

  const handleLogin = (token) => {
    localStorage.setItem('token', token);
    setToken(token);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
  };

  return (
    <BrowserRouter>
      <header className="bg-blue-700 text-white py-4 mb-8 shadow">
        <nav className="container mx-auto flex justify-between items-center px-4">
          <Link to="/" className="text-2xl font-bold">Event Management</Link>
          <div className="space-x-4">
            <Link to="/" className="hover:underline">Events</Link>
            {token ? (
              <button onClick={handleLogout} className="bg-white text-blue-700 px-3 py-1 rounded hover:bg-gray-100 transition">Logout</button>
            ) : (
              <>
                <Link to="/login" className="hover:underline">Login</Link>
                <Link to="/register" className="hover:underline">Register</Link>
              </>
            )}
          </div>
        </nav>
      </header>
      <Routes>
        <Route path="/" element={<EventList token={token} />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register onLogin={handleLogin} />} />
        <Route path="/events/:id" element={<EventDetails token={token} />} />
        <Route path="/create" element={token ? <EventForm token={token} /> : <Navigate to="/login" />} />
        <Route path="/edit/:id" element={token ? <EventForm token={token} edit /> : <Navigate to="/login" />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
