import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function EventList({ token }) {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/events')
      .then(res => setEvents(res.data));
  }, []);

  return (
    <div className="max-w-4xl mx-auto mt-10">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Events</h1>
        {token && (
          <Link
            to="/create"
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            Create Event
          </Link>
        )}
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {events.map(event => (
          <div key={event._id} className="bg-white shadow rounded p-4 flex flex-col">
            <Link to={`/events/${event._id}`} className="text-xl font-semibold text-blue-700 hover:underline mb-2">
              {event.title || 'Untitled'}
            </Link>
            <p className="text-gray-600 mb-4">by {event.createdBy?.username || 'Unknown'}</p>
            <Link
              to={`/events/${event._id}`}
              className="mt-auto text-blue-600 hover:underline"
            >
              View Details
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}
