import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function EventList({ token }) {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/events')
      .then(res => setEvents(res.data));
  }, []);

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-[#7f5af0] via-[#232946] to-black flex flex-col items-center py-16 px-2">
      <div className="w-full max-w-6xl">
        <h1 className="text-5xl md:text-7xl font-extrabold text-white mb-8 drop-shadow-lg tracking-tight text-center">
          Event Management
        </h1>
        <div className="flex flex-wrap justify-center gap-8 mt-12">
          {events.length === 0 && (
            <div className="text-lg text-white/80">No events found.</div>
          )}
          {events.map(event => (
            <div
              key={event._id}
              className="relative bg-[#18181b]/90 rounded-3xl shadow-2xl p-8 w-80 flex flex-col items-start border border-white/10 hover:scale-105 transition-transform duration-300"
            >
              <Link to={`/events/${event._id}`} className="text-2xl font-bold text-[#7f5af0] hover:underline mb-2">
                {event.title || 'Untitled'}
              </Link>
              <p className="text-white/80 mb-6 min-h-[48px]">{event.description?.slice(0, 80) || 'No description.'}</p>
              <div className="flex items-center justify-between w-full text-white/60 text-sm mb-2">
                <span>by {event.createdBy?.username || 'Unknown'}</span>
                <span>{event.date ? new Date(event.date).toLocaleDateString() : ''}</span>
              </div>
              <Link
                to={`/events/${event._id}`}
                className="mt-2 inline-block text-[#7f5af0] hover:underline font-semibold"
              >
                View Details
              </Link>
              {/* Optional: Add avatars or icons here for extra polish */}
            </div>
          ))}
        </div>
        {token && (
          <Link
            to="/create"
            className="fixed bottom-8 right-8 z-50 bg-[#7f5af0] text-white rounded-full shadow-lg p-5 flex items-center justify-center text-3xl hover:bg-[#6241c7] transition"
            title="Create Event"
          >
            +
          </Link>
        )}
      </div>
    </div>
  );
}
