import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function EventList({ token }) {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/events')
      .then(res => setEvents(res.data));
  }, []);

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-5xl mx-auto py-10 px-4">
        <div className="flex items-center justify-between mb-8">
          <h1 className="text-4xl font-extrabold text-gray-800">Events</h1>
          {token && (
            <Link
              to="/create"
              className="inline-flex items-center px-5 py-2.5 bg-blue-600 text-white font-semibold rounded-full shadow hover:bg-blue-700 transition"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" d="M12 4v16m8-8H4" />
              </svg>
              Create Event
            </Link>
          )}
        </div>
        <div className="grid gap-8 grid-cols-1 sm:grid-cols-2 md:grid-cols-3">
          {events.length === 0 && (
            <div className="col-span-full text-center text-gray-500">No events found.</div>
          )}
          {events.map(event => (
            <div key={event._id} className="bg-white rounded-xl shadow hover:shadow-lg transition p-6 flex flex-col">
              <Link to={`/events/${event._id}`} className="text-2xl font-bold text-blue-700 hover:underline mb-2">
                {event.title || 'Untitled'}
              </Link>
              <p className="text-gray-600 flex-1 mb-4">{event.description?.slice(0, 80) || 'No description.'}</p>
              <div className="flex items-center justify-between text-sm text-gray-500">
                <span>by {event.createdBy?.username || 'Unknown'}</span>
                <span>{event.date ? new Date(event.date).toLocaleDateString() : ''}</span>
              </div>
              <Link
                to={`/events/${event._id}`}
                className="mt-4 inline-block text-blue-600 hover:underline font-medium"
              >
                View Details
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
